import { FC, useCallback, useRef, useState } from 'react';
import styles from './PizzaBlock.module.scss';
import cl from 'classnames';
import { addToCart } from '../../redux/slices/cartSlice';
import { useDispatch, useSelector } from 'react-redux';
import { Button, Flex, Card, Radio } from 'antd';
import type { CheckboxOptionType, RadioChangeEvent } from 'antd';
import { CheckboxValueType } from 'antd/es/checkbox/Group';
// import { Input, Radio, Space } from 'antd';

type PizzaBlockProps = {
  id: string;
  imageUrl: string;
  price: number;
  sizes: number[];
  title: string;
  types: number[];
};

export const PizzaBlock: FC<PizzaBlockProps> = ({ id, imageUrl, price, sizes, title, types }) => {
  const pizzaTypes = ['Тонкое', 'Традиционное'];
  const firstRenderPizzaTypes = types.map((item) => pizzaTypes[item]);
  // const firstRenderPizzaSizes = sizes.

  const [activeType, setActiveType] = useState(0);
  const [activeSize, setActiveSize] = useState(0);

  const realSize = sizes[activeSize];
  const realId = useRef(id);

  const dispatch = useDispatch();
  const items = useSelector((state: any) => state.cart.items);

  const { Meta } = Card;

  console.log(types);

  const cartClick = () => {
    dispatch(
      addToCart({
        id: realId.current,
        imageUrl,
        price,
        activeSize: realSize,
        title,
        activeType: pizzaTypes[activeType],
        count: 1,
      }),
    );
  };

  type Option = (string | number | CheckboxOptionType<CheckboxValueType>) | undefined;

  const onChangeType = ({ target: { value } }: RadioChangeEvent) => {
    console.log('radio1 checked', value);
    setActiveType(value);
  };

  const onChangeSize = ({ target: { value } }: RadioChangeEvent) => {
    console.log('radio2 checked', value);
    setActiveSize(value);
  };

  return (
    // <>
    //   <div className={styles.pizza_container}>
    //     <div>
    //       <img className={styles.img} src={imageUrl} alt={title} />
    //     </div>
    //     <div>
    //       <h3 className={styles.h3}>{title}</h3>
    //       <div className={styles.switch}>
    //         <div className={styles.switch__type}>
    //           {types.map((type, index) => (
    //             <span
    //               key={index}
    //               onClick={() => setActiveType(index)}
    //               className={
    //                 activeType === index
    //                   ? cl(styles.type__span, styles.type__span_active)
    //                   : cl(styles.type__span)
    //               }>
    //               {pizzaTypes[type]}
    //             </span>
    //           ))}
    //         </div>
    //         <div className={styles.switch__size}>
    //           {sizes.map((size, index) => (
    //             <span
    //               key={index}
    //               onClick={() => setActiveSize(index)}
    //               className={
    //                 activeSize === index
    //                   ? cl(styles.size__span, styles.size__span_active)
    //                   : cl(styles.size__span)
    //               }>
    //               {' '}
    //               {size}{' '}
    //             </span>
    //           ))}
    //         </div>
    //       </div>
    //       <div className={styles.price_and_button}>
    //         <span>от {price} Р</span>
    //         <Button onClick={cartClick}>
    //         <span>Добавить </span>
    //         </Button >
    //         {/* <button onClick={cartClick} className={styles.button}>
    //           <span>Добавить </span>
    //         </button> */}
    //       </div>
    //     </div>
    //   </div>
    // </>
    // onChange={ (e: RadioChangeEvent) => setActiveType(e.target.value)}

    <Card hoverable style={{ width: 300 }} cover={<img alt={title} src={imageUrl} />}>


      <Flex justify="space-between" align="start" vertical gap='middle'>
      <Meta title={title} />

        <Radio.Group
          options={firstRenderPizzaTypes}
          onChange={onChangeType}
          value={activeType}
          optionType="button"
        />

        <Radio.Group
          options={sizes}
          onChange={onChangeSize}
          value={activeSize}
          optionType="button"
        />

      </Flex>

      <Flex justify="space-between" align="center">
        <div>от {price} Р</div>
        <Button onClick={cartClick}>
          <span>Добавить </span>
        </Button>
      </Flex>
    </Card>
  );
};
